
%% Load libraries 
sca;
close all;     
clear;

addpath libs/cnsp_utils
addpath libs/cnsp_utils/cnd
addpath libs/mTRF-Toolbox_v2/mtrf
addpath libs/NoiseTools
addpath libs/eeglab
eeglab

disp('Libraries loaded');

%% Prepping the EEG data and chunking the stimulus 

% butterworth for low pass filterings
% filter before epoching

disp('Reading in and prepping EEG data and stimuli');


fileName = ['./datasets/EEGDataBDF/' , 'subject1.bdf'];
% fileName = ['./datasets/EEGDataBDF/' , 'subject2.bdf'];
% fileName = ['./datasets/EEGDataBDF/' , 'subject3_3.bdf'];
% fileName = ['./datasets/EEGDataBDF/' , 'subject4_1.bdf'];
% fileName = ['./datasets/EEGDataBDF/' , 'subject5_1.bdf'];

[EEG_raw, trigs] = read_bdf(fileName);
fsEEG = 512;

% Save the EEG data in one variable
EEG_raw = EEG_raw(1:66,:); % 64 elec + 2 mastoids

BUTTON_PRESS_TRIG = 3;
STIMULUS_ONSET_TRIG = 1;

% Save the triggers in another variable
trigs=trigs-min(trigs);
trigs(trigs>256) = trigs(trigs>256)-min(trigs(trigs>256));
dtrig = diff(trigs); 
dtrig(dtrig < 0) = 0; %remove any 0 triggers 

stimulusOnsets = find(dtrig == STIMULUS_ONSET_TRIG);
buttonPresses = find(dtrig == BUTTON_PRESS_TRIG);

nTrials = length(stimulusOnsets); % One stimulus onset per trial so this stimuluOnsets will contains the number of trials

