% Getting trial length and set up stimulus structure
trialDurSec = zeros(1, nTrials);
fsDown = 128;
clear stimInOrder
for trialNo = 1:nTrials
    disp(trialNo);
    [audio, fsStimulus] = audioread(sounds{trialNo});
    trialDurSec(trialNo) = size(audio,1)/fsStimulus;
    stimInOrder.data{1,trialNo} = resample(abs(hilbert(audio(:,1))),fsDown,fsStimulus);
end
stimInOrder.names = {'Envelope'};
stimInOrder.fs = fsDown;

% save('./datasets/linguisticDecision/dataStimInOrder.mat','stimInOrder')
% disp(stimInOrder.data);

buttonPressSamp = {};
for pressNum = 1:nTrials
    
    buttonPressSamp{pressNum} = buttonPresses(pressNum) - stimulusOnsets(pressNum); 
end

disp('End of section one');

%% Filtering and saving the EEG data 

disp('Filtering EEG data');
% Filtering EEG data

bandpassFilterRange = [1, 10];
EEG_raw = EEG_raw';

hd = getLPFilt(fsEEG,bandpassFilterRange(2));
EEG_raw = filtfilthd(hd,EEG_raw);
disp('here');
hd = getHPFilt(fsEEG,bandpassFilterRange(1));
EEG_raw = filtfilthd(hd,EEG_raw);

disp('here')
% Downsample EEG data by a factor of 4 (512Hz -> 128Hz = * 1/4) 

% Set up EEG structure in CND format
clear eeg
eeg.data = {};
eeg.extChan = {};
eeg.extChan{1}.data = {};
eeg.buttonpress = {};
% extChan = isfield(eeg,'extChan');
for trialNum = 1:nTrials  
    disp(trialNum);
    % A trial length is defined as the length from when the audio beings (stimulus onset) to the end of the audio (audio length)
    startTrial = stimulusOnsets(trialNum);
    endTrial = startTrial + round(trialDurSec(trialNum) * fsEEG) - 1;
    
    eeg.data{end+1} = downsample(EEG_raw(startTrial:endTrial,1:64),fsEEG/fsDown);
    eeg.buttonpress{trialNum} = round(buttonPressSamp{trialNum}/(fsEEG/fsDown));
    eeg.extChan{1}.data{end+1} = downsample(EEG_raw(startTrial:endTrial,65:66),fsEEG/fsDown);
    eeg.extChan{1}.description = 'Mastoids';
end
eeg.fs = fsDown;



