fileNames = {'./datasets/EEGDataBDF/subject1.bdf', './datasets/EEGDataBDF/subject2.bdf', './datasets/EEGDataBDF/subject3.bdf', './datasets/EEGDataBDF/subject4.bdf', './datasets/EEGDataBDF/subject5.bdf', './datasets/EEGDataBDF/subject6.bdf'};
stimuliOrder = {soundsP1, soundsP2, soundsP3, soundsP4, soundsP5, soundsP6};

numParticpants = length(fileNames);

EEG_raw_array = {1, numParticpants}; stimulusOnsets = {1, numParticpants}; buttonPresses = {1, numParticpants};
for participantNo = 1:numParticpants
    
    [EEG_raw, trigs] = read_bdf(fileNames{participantNo});
    fsEEG = 512;
    
    % Save the EEG data in one variable
    EEG_raw_array{participantNo} = EEG_raw(1:66,:); % 64 elec + 2 mastoids
    
    BUTTON_PRESS_TRIG = 3;
    STIMULUS_ONSET_TRIG = 1;

    % Save the triggers in another variable
    trigs=trigs-min(trigs);
    trigs(trigs>256) = trigs(trigs>256)-min(trigs(trigs>256));
    dtrig = diff(trigs); 
    dtrig(dtrig < 0) = 0; %remove any 0 triggers 

    stimulusOnsets{participantNo} = find(dtrig == STIMULUS_ONSET_TRIG);
    buttonPresses{participantNo} = find(dtrig == BUTTON_PRESS_TRIG);
    
    disp(['Finished participant ' num2str(participantNo)]);
end

nTrials = length(stimulusOnsets{1}); % One stimulus onset per trial so this stimuluOnsets will contains the number of trials

% Getting trial length and set up stimulus structure
trialDurSec = zeros(numParticpants, nTrials);
fsDown = 128;
buttonPressSamp = {1, numParticpants};

% Initialise a cell array of structs to store the stimulus data
stimData = cell(1, numParticpants);
for participantNo = 1:numParticpants
    stimData{participantNo}.data = cell(1, nTrials);
    stimData{participantNo}.names = {'Envelope'};
    stimData{participantNo}.fs = fsDown;
end

for participantNo = 1:numParticpants
    for trialNo = 1:nTrials
        
        [audio, fsStimulus] = audioread(stimuliOrder{participantNo}{trialNo}); % Read in stimulus

        trialDurSec(participantNo, trialNo) = size(audio,1)/fsStimulus; % Calculate length of stimulus
        
        resampledData = resample(abs(hilbert(audio(:,1))),fsDown,fsStimulus); % Downsample data
        
        stimData{participantNo}.data{1,trialNo} = resampledData; % Store data in a struct 
            
        buttonPressSamp{participantNo}(trialNo) = buttonPresses{participantNo}(trialNo) - stimulusOnsets{participantNo}(trialNo); % Store relative button press

    end
end

disp('Finished creating stimulus structures');

%% Filtering and saving the EEG data 

disp('Filtering EEG data');

for participantNo = 1:numParticpants
    
    disp(participantNo);

    bandpassFilterRange = [1, 10]; % Auditory response and full sequecne ERP
%     bandpassFilrerRange = [12, 36]; % Motor preparation
%     bandpassFilterLow = 10; % For the low pass filter with no high pass filtering 
    
    EEG_raw_array{participantNo} = EEG_raw_array{participantNo}';
    
    hd = getLPFilt(fsEEG,bandpassFilterRange(2));
    EEG_raw_array{participantNo} = filtfilthd(hd,EEG_raw_array{participantNo});
    
    hd = getHPFilt(fsEEG,bandpassFilterRange(1));
    EEG_raw_array{participantNo} = filtfilthd(hd,EEG_raw_array{participantNo});
    
    disp(['Finished participant ' num2str(participantNo)]);

end 

disp('Finished filtering EEG data');

%% ******************* SAVE EEG DATA INTO STRUCTS ****************** 

% Initialise a cell array of structs to store the EEG data
eegData = cell(1, numParticpants);
for participantNo = 1:numParticpants
    eegData{participantNo}.data = {};
    eegData{participantNo}.buttonpress = {};
    eegData{participantNo}.extChan = {};
    eegData{participantNo}.extChan{1}.data = {};
    eegData{participantNo}.fs = fsDown;
    eegData{participantNo}.extChan{1}.description = 'Mastoids'; % Change this if changing the re-referencing type
end

% Initialise a cell array of structs to store the EEG data (for motor preparation only)
% betaData = cell(1, numParticpants);
% for participantNo = 1:numParticpants
%     betaData{participantNo}.data = {};
%     betaData{participantNo}.buttonpress = {};
%     betaData{participantNo}.extChan = {};
%     betaData{participantNo}.extChan{1}.data = {};
%       betaData{participantNo}.fs = fsDown;
%       betaData{participantNo}.extChan{1}.description = 'Mastoids';
% end 

for participantNo = 1:numParticpants
    for trialNum = 1:nTrials  

        % A trial length is defined as the length from when the audio beings (stimulus onset) to the end of the audio (audio length)
        startTrial = stimulusOnsets{participantNo}(trialNum);
        
        if (trialNum == 272)
            endTrial = startTrial + round(trialDurSec(participantNo, trialNum) * fsEEG) - 1;
        else 
            endTrial = stimulusOnsets{participantNo}(trialNum+1);
        end

        % Downsample EEG data
        eegData{participantNo}.data{end+1} = downsample(EEG_raw_array{participantNo}(startTrial:endTrial,1:64),fsEEG/fsDown); 
        % Store button press sample times and downsample them
        eegData{participantNo}.buttonpress{trialNum} = round(buttonPressSamp{participantNo}(trialNum)/(fsEEG/fsDown));
        % Store the external channels i.e. electrode 65 and 66, the mastoids, in the external channels section of the struct
        eegData{participantNo}.extChan{1}.data{end+1} = downsample(EEG_raw_array{participantNo}(startTrial:endTrial,65:66),fsEEG/fsDown);  
    end
    
    disp(['Finished participant ' num2str(participantNo)]);

end

reRefType = 'Mastoids'; % 'Avg' Global average or 'Mastoids' referencing
% Re-refernece the EEG data to either the mastoids of global average
for participantNo = 1:numParticpants
    
    eegData{participantNo} = cndReref(eegData{participantNo},reRefType);
    
end

disp('Finished downsampling and storing EEG data into its structures');

