    for i = 1:nTrials
        assert(isequal( size(stimulusEachParticipant{participantNo}.data{1, i}), size((stimulusEachParticipant{participantNo}.data{2, i})),...
        size((stimulusEachParticipant{participantNo}.data{3, i}))));
    end
end

disp('FINSIHED SECTION 1: Sectioned stimuli into meanings, sequence numbers and acroynms');

%% *********************************************** 2) WORD ONSET ANALYSIS ****************************************************************

%% 2.1) ERP of the word onset 

% Window of interest in samples = [13, 64] : round(0.1*fsDown), round(0.5*fsDown)
windowOfInterest = [0.1, 1];
preStimBaselineWindow = [0.1, 0.15];

% windowOfInterest = [0.4, 1];
% preStimBaselineWindow = [0.4, 0.5];

selectedElectrodes = [19, 32, 56];

onsetEEGdata_eachWord_Ambig = cell(nTrials, numAudiosInSeq); onsetEEGdata_eachWord_Meaning = cell(nTrials, numAudiosInSeq);
onsetEEGdata_ambiguousWords = {1, numParticipants}; onsetEEGdata_meaningWords = {1, numParticipants};
for participantNo = 1:numParticipants
    for eegDataNo = 1:nTrials

        sampleNumOnsets = find(stimulusEachParticipant{participantNo}.data{2, eegDataNo} > 0);

        for audioNum = 1:numAudiosInSeq
            
            % Uncomment for baseline 400ms plot
%             preStimulus = round(sampleNumOnsets(audioNum) + (windowOfInterest(1) * fsDown));
            preStimulus = round(sampleNumOnsets(audioNum) - (windowOfInterest(1) * fsDown));
            postStimulus = round(sampleNumOnsets(audioNum) + (windowOfInterest(2) * fsDown));

            acronymName = seqAcronym{participantNo, eegDataNo}; % Acronym e.g. US
            tableNum = acronymToIndexNumber(acronymName);
            sequenceNumber = sequenceNum{participantNo, eegDataNo}; % Sequence number e.g. 16
            
            ambigVmeanWords = tablesAvMWords{tableNum}{:, str2double(sequenceNumber)};
            trialLengthSamp = round(table2array(arrayOfTables{tableNum}(9, str2double(sequenceNumber)+1)));

            remainingTrialDuration = trialLengthSamp - sampleNumOnsets(audioNum);
            if (remainingTrialDuration < windowOfInterest(2) * fsDown)
                disp('Word length is shorter than the analysis window. Skipping word.');
                continue;
            end
            
%             Baseline correction
            baselineStart = preStimulus;
            baselineEnd = round(sampleNumOnsets(audioNum) + (preStimBaselineWindow(2) * fsDown));
            
            baselineData = EEGDataEachParticipant{participantNo}.data{eegDataNo}(baselineStart:baselineEnd, selectedElectrodes);
            baselineVoltage = mean(baselineData,1);
            
            if (postStimulus > length(EEGDataEachParticipant{participantNo}.data{eegDataNo}))
            
                sampleDiff = postStimulus - length(EEGDataEachParticipant{participantNo}.data{eegDataNo});
                EEGDataEachParticipant{participantNo}.data{eegDataNo} = padarray(EEGDataEachParticipant{participantNo}.data{eegDataNo}, (sampleDiff), 0,'post');
            end
            
            if (ambigVmeanWords{audioNum}) == 'A'  % if the word is ambiguous put it in the ambiguous words cell array
                onsetEEGdata_eachWord_Ambig{eegDataNo, audioNum} = EEGDataEachParticipant{participantNo}.data{eegDataNo}(preStimulus:postStimulus, selectedElectrodes) - baselineVoltage;
            elseif (ambigVmeanWords{audioNum}) == 'M'  % e,se if the word is not ambiguous put it in the meaning words cell array
                onsetEEGdata_eachWord_Meaning{eegDataNo, audioNum} = EEGDataEachParticipant{participantNo}.data{eegDataNo}(preStimulus:postStimulus, selectedElectrodes) - baselineVoltage;
            end

        end
    end
    
    onsetEEGdata_ambiguousWords{participantNo} = onsetEEGdata_eachWord_Ambig;
    onsetEEGdata_meaningWords{participantNo} = onsetEEGdata_eachWord_Meaning;
    
end

% % For 64 electrodes
averageERPsAmbig = {1, numParticipants}; averageERPsMeaning = {1, numParticipants}; 
for participantNo = 1:numParticipants
        % Average ERPs and electrodes here
        averageERPsAmbig{participantNo} = mean(cat(3, onsetEEGdata_ambiguousWords{participantNo}{:}), 3);  % Get the avg ERP per participant over average of 3 electrodes 
       
        averageERPsMeaning{participantNo} = mean(cat(3, onsetEEGdata_meaningWords{participantNo}{:}), 3); % Get the avg ERP per participant over average of 3 electrodes 
            
end

%% *** FOR SELECTED ELECTRODES ONLY CPZ, CP1, CP2, NOT FOR 64 ELECTRODES ***
% For selected electrodes 
averageERPsAmbig = {1, numParticipants}; averageERPsMeaning = {1, numParticipants}; 
for participantNo = 1:numParticipants
        % Average ERPs and electrodes here
        averageERPsAmbig{participantNo} = mean(mean(cat(3, onsetEEGdata_ambiguousWords{participantNo}{:}), 3), 2);  % Get the avg ERP per participant over average of 3 electrodes 
       
        averageERPsMeaning{participantNo} = mean(mean(cat(3, onsetEEGdata_meaningWords{participantNo}{:}), 3), 2); % Get the avg ERP per participant over average of 3 electrodes 
            
end

% Calculate the SEM for ambig
avgERPsMatrix_Ambig = cell2mat(averageERPsAmbig)'; % Put the ERPs in a matrix of size 6x142 (6 participants = 6 rows, time - 142 sample columns)
SEMAmbigColumns = std(avgERPsMatrix_Ambig, 0, 1)/sqrt(numParticipants); % Get the SEM of each column in the matrix - std dev of column / sqrt(num participants)

% Calculate the SEM for meaning
avgERPsMatrix_Meaning = cell2mat(averageERPsMeaning)'; % Put the ERPs in a matrix of size 6x142 (6 participants = 6 rows, time - 142 sample columns)
SEMMeaningColumns = std(avgERPsMatrix_Meaning, 0, 1)/sqrt(numParticipants); % Get the SEM of each column in the matrix - std dev of column / sqrt(num participants)

%% Getting avergae ERP

% Get the average ERP of all participants for ambiguous and meaning
finalAvgERP_Ambig = (averageERPsAmbig{1} + averageERPsAmbig{2} + averageERPsAmbig{3} + averageERPsAmbig{4} + averageERPsAmbig{5} + averageERPsAmbig{6})/numParticipants;
finalAvgERP_Meaning = (averageERPsMeaning{1} + averageERPsMeaning{2} + averageERPsMeaning{3} + averageERPsMeaning{4} + averageERPsMeaning{5} + averageERPsMeaning{6})/numParticipants;

disp('Finished caluclating ERP of the word onset for ambiguous vs non-ambiguous words and getting the standard error of both');

