% Later a 1 will be added to .data{2, colNum} at a sample numbers corresponding to the times a word audio occurs in a stimulus
% Later a 1 will be added to .data{3, colNum} at a sample number corresponding to the time that a decision was made in each trial.
columns = length(stimP1.data);
for participantNo = 1:numParticipants
    for colNum = 1:columns
        stimulusEachParticipant{participantNo}.data{2,colNum} = zeros(size(stimulusEachParticipant{participantNo}.data{1,colNum}));
        stimulusEachParticipant{participantNo}.data{3,colNum} = zeros(size(stimulusEachParticipant{participantNo}.data{1,colNum}));
    end
end

% Read in MATLAB generated audio onset files and store them in a large table for access later
audioOnsetFilepaths = {'./datasets/AudioOnsetTimings_Matlab/AppleOrange_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/BedCouch_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/BeeWasp_AudioOnsetTiming.csv','./datasets/AudioOnsetTimings_Matlab/BusTrain_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/CarBus_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/CarrotPotato_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/CatDog_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/ChickenPork_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/FootballRugby_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/GoldSilver_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/HouseApartment_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/MovieBook_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/TshirtShirt_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/UniversitySchool_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/WaterMilk_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/RainSnow_AudioOnsetTiming.csv', './datasets/AudioOnsetTimings_Matlab/TelevisionSmartphone_audioOnsetTiming.csv'};                                                                                                                                                                                                                                                                                                                                                                                                                                               
numFiles = length(audioOnsetFilepaths);
arrayOfTables = {1,length(audioOnsetFilepaths)};
for i = 1:numFiles
    
    arrayOfTables{i} = readtable(audioOnsetFilepaths{i}, 'ReadVariableNames', false);

end

% Read in ambiguous versus meaning words CSV creating using a Python script
ambigVsMeaningFilepaths = {'./datasets/AmbigVsNonAmbigCSVs/AppleOrange_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/BedCouch_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/BeeWasp_MvA.csv','./datasets/AmbigVsNonAmbigCSVs/BusTrain_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/CarBus_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/CarrotPotato_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/CatDog_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/ChickenPork_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/FootballRugby_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/GoldSilver_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/HouseApartment_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/MovieBook_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/TshirtShirt_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/UniversitySchool_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/WaterMilk_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/RainSnow_MvA.csv', './datasets/AmbigVsNonAmbigCSVs/TelevisionSmartphone_MvA.csv'};                                                                                                                                                                                                                                                                                                                                                                                                                                               
numFiles = length(ambigVsMeaningFilepaths);
tablesAvMWords = {1,length(ambigVsMeaningFilepaths)};
for i = 1:numFiles
    
    tablesAvMWords{i} = readtable(ambigVsMeaningFilepaths{i}, 'ReadVariableNames', false);

end

disp('FINISHED SECTION I: Read in clean EEG data and processed audio onset files');

%% ******************************************* 1) BY WORD (NOT SENTENCE) ANALYSIS PRE-PROCESSING STEP ************************************************

%% Run this when not running sentence ERP. Section that ultimately creates a cell array contains the audio onset times in samples for each audio onset in each audio file

% Breaks the files names into 3 parts: acronym, meaning number and sequence number and stores them in arrays. Then using these arrays obtains the 
% audio onsets and stores them in the the second section of stim.data.

% Code to split up the sound names into acronym, meaning and sequence number
seqAcronym= cell(numParticipants,nTrials);
meaningNum= cell(numParticipants,nTrials);
sequenceNum= cell(numParticipants,nTrials);

for participantNo = 1:numParticipants
    for audioFileName = 1:nTrials

        brokenString = strsplit(stimuliOrder{participantNo}{audioFileName},'_'); % Break string on underscores - breaks 3 pieces

        upToAcronym = strsplit(brokenString{1}, '/'); % Breaks acronym string from .../AllRecordings/US by the forward slashes
        seqNoWav = strsplit(brokenString{3}, '.'); % Removes the .wav from seqNo.wav

        seqAcronym{participantNo, audioFileName} = upToAcronym{end}; % Acronym e.g. US
        meaningNum{participantNo, audioFileName} = brokenString{2}; % Meaning number e.g. M2
        sequenceNum{participantNo, audioFileName} = seqNoWav{1}; % Sequence number e.g. 16

    end
end

% Code for obtaining the audio onsets and then storing them in stim.data
for participantNo = 1:numParticipants
    for onsetAudioNum = 1:numAudiosInSeq
        for stringValue = 1:nTrials

            tableNumIndex = acronymToIndexNumber(seqAcronym{participantNo, stringValue});
            columnNum = str2double(sequenceNum{participantNo, stringValue}) + 1;

            onsetInSamples = arrayOfTables{tableNumIndex}{onsetAudioNum, columnNum};

            stimulusEachParticipant{participantNo}.data{2, stringValue}(onsetInSamples) = 1;
        end
    end
end

% Check that there was no unintentional extra data added
for participantNo = 1:numParticipants
    for i = 1:nTrials
        assert(isequal( size(stimulusEachParticipant{participantNo}.data{1, i}), size((stimulusEachParticipant{participantNo}.data{2, i})),...
        size((stimulusEachParticipant{participantNo}.data{3, i}))));
    end
end

disp('FINSIHED SECTION 1: Sectioned stimuli into meanings, sequence numbers and acroynms');

%% *********************************************** 2) WORD ONSET ANALYSIS ****************************************************************

%% 2.1) ERP of the word onset 

% Window of interest and baseline window for normal ERP
windowOfInterest = [0.1, 1];
preStimBaselineWindow = [0.1, 0.15];

% Window of interest and baseline window for shorter ERP baselined at 400ms - 500ms
% windowOfInterest = [0.4, 1];
% preStimBaselineWindow = [0.4, 0.5];

% Locations of three electrodes centred on CPz
selectedElectrodes = [19, 32, 56];

onsetEEGdata_eachWord_Ambig = cell(nTrials, numAudiosInSeq); onsetEEGdata_eachWord_Meaning = cell(nTrials, numAudiosInSeq);
onsetEEGdata_ambiguousWords = {1, numParticipants}; onsetEEGdata_meaningWords = {1, numParticipants};
for participantNo = 1:numParticipants
    for eegDataNo = 1:nTrials

        sampleNumOnsets = find(stimulusEachParticipant{participantNo}.data{2, eegDataNo} > 0);

        for audioNum = 1:numAudiosInSeq
            
            % Use this preStimulus for the baseline 400ms ERP
            % preStimulus = round(sampleNumOnsets(audioNum) + (windowOfInterest(1) * fsDown));
            % Use this preStimulus for the normal ERP
            preStimulus = round(sampleNumOnsets(audioNum) - (windowOfInterest(1) * fsDown));
            postStimulus = round(sampleNumOnsets(audioNum) + (windowOfInterest(2) * fsDown));

            acronymName = seqAcronym{participantNo, eegDataNo}; % Acronym e.g. US
            tableNum = acronymToIndexNumber(acronymName);
            sequenceNumber = sequenceNum{participantNo, eegDataNo}; % Sequence number e.g. 16
            
            ambigVmeanWords = tablesAvMWords{tableNum}{:, str2double(sequenceNumber)};
            trialLengthSamp = round(table2array(arrayOfTables{tableNum}(9, str2double(sequenceNumber)+1)));

            remainingTrialDuration = trialLengthSamp - sampleNumOnsets(audioNum);
            if (remainingTrialDuration < windowOfInterest(2) * fsDown) 
                continue;
            end
            
            % Baseline correction
            baselineStart = preStimulus;
            baselineEnd = round(sampleNumOnsets(audioNum) + (preStimBaselineWindow(2) * fsDown));
            
            baselineData = EEGDataEachParticipant{participantNo}.data{eegDataNo}(baselineStart:baselineEnd, selectedElectrodes);
            baselineVoltage = mean(baselineData,1);
            
            if (postStimulus > length(EEGDataEachParticipant{participantNo}.data{eegDataNo}))
            
                sampleDiff = postStimulus - length(EEGDataEachParticipant{participantNo}.data{eegDataNo});
                EEGDataEachParticipant{participantNo}.data{eegDataNo} = padarray(EEGDataEachParticipant{participantNo}.data{eegDataNo}, (sampleDiff), 0,'post');
            end
            
            if (ambigVmeanWords{audioNum}) == 'A'  % if the word is ambiguous put it in the ambiguous words cell array
                onsetEEGdata_eachWord_Ambig{eegDataNo, audioNum} = EEGDataEachParticipant{participantNo}.data{eegDataNo}(preStimulus:postStimulus, selectedElectrodes) - baselineVoltage;
            elseif (ambigVmeanWords{audioNum}) == 'M'  % e,se if the word is not ambiguous put it in the meaning words cell array
                onsetEEGdata_eachWord_Meaning{eegDataNo, audioNum} = EEGDataEachParticipant{participantNo}.data{eegDataNo}(preStimulus:postStimulus, selectedElectrodes) - baselineVoltage;
            end

        end
    end
    
    onsetEEGdata_ambiguousWords{participantNo} = onsetEEGdata_eachWord_Ambig;
    onsetEEGdata_meaningWords{participantNo} = onsetEEGdata_eachWord_Meaning;
    
end

disp('Finished dividing out EEG data into meaning and ambiguous words');

%% ************************** FOR 64 ELECTRODES ONLY  ****************************

% % For 64 electrodes
averageERPsAmbig = {1, numParticipants}; averageERPsMeaning = {1, numParticipants}; 
for participantNo = 1:numParticipants
        % Average ERPs and electrodes here
        averageERPsAmbig{participantNo} = mean(cat(3, onsetEEGdata_ambiguousWords{participantNo}{:}), 3);  % Get the avg ERP per participant over average of 3 electrodes 
        averageERPsMeaning{participantNo} = mean(cat(3, onsetEEGdata_meaningWords{participantNo}{:}), 3); % Get the avg ERP per participant over average of 3 electrodes 
            
end

disp('Finished calculating ERP for 64 electrodes');

%% ************************** FOR SELECTED ELECTRODES ONLY CPZ, CP1, CP2, NOT FOR 64 ELECTRODES ****************************

averageERPsAmbig = {1, numParticipants}; averageERPsMeaning = {1, numParticipants}; 
for participantNo = 1:numParticipants
        % Average ERPs and electrodes here
        averageERPsAmbig{participantNo} = mean(mean(cat(3, onsetEEGdata_ambiguousWords{participantNo}{:}), 3), 2);  % Get the avg ERP per participant over average of 3 electrodes 
        averageERPsMeaning{participantNo} = mean(mean(cat(3, onsetEEGdata_meaningWords{participantNo}{:}), 3), 2); % Get the avg ERP per participant over average of 3 electrodes 
            
end

% Calculate the SEM for ambig
avgERPsMatrix_Ambig = cell2mat(averageERPsAmbig)'; % Put the ERPs in a matrix of size 6x142 (6 participants = 6 rows, time - 142 sample columns)
SEMAmbigColumns = std(avgERPsMatrix_Ambig, 0, 1)/sqrt(numParticipants); % Get the SEM of each column in the matrix - std dev of column / sqrt(num participants)

% Calculate the SEM for meaning
avgERPsMatrix_Meaning = cell2mat(averageERPsMeaning)'; % Put the ERPs in a matrix of size 6x142 (6 participants = 6 rows, time - 142 sample columns)
SEMMeaningColumns = std(avgERPsMatrix_Meaning, 0, 1)/sqrt(numParticipants); % Get the SEM of each column in the matrix - std dev of column / sqrt(num participants)

disp('Finished calculating ERP for selected electrodes');

%% ********************************* FINAL AVERAGE ERP ************************************************

% Get the average ERP of all participants for ambiguous and meaning
finalAvgERP_Ambig = (averageERPsAmbig{1} + averageERPsAmbig{2} + averageERPsAmbig{3} + averageERPsAmbig{4} + averageERPsAmbig{5} + averageERPsAmbig{6})/numParticipants;
finalAvgERP_Meaning = (averageERPsMeaning{1} + averageERPsMeaning{2} + averageERPsMeaning{3} + averageERPsMeaning{4} + averageERPsMeaning{5} + averageERPsMeaning{6})/numParticipants;

disp('Finished caluclating ERP of the word onset for ambiguous vs non-ambiguous words and getting the standard error of both');

