sumEEGDataP6 = cat(3, reponseEEGData_eachWordP6{:});
avgEEGDataP6 = mean(sumEEGDataP6, 3);

finalAvgEEGData = (avgEEGDataP1 + avgEEGDataP2 + avgEEGDataP3 + avgEEGDataP4 + avgEEGDataP5 + avgEEGDataP6)/numParticipants;

