plot(time_axis_long, leftBrain_minus_rightBrain, 'LineWidth', 1.5, 'Color', 'r');
xlim([time_axis_long(1), time_axis_long(end)]);
xlabel('Time (ms)')
ylabel('Magnitude (a.u.)')
